mapper.select:
select ai.id, asset_name, version, status, is_lend, is_take, is_visible, w.name from asset_info ai, warehouse w
where ai.warehouse_id = w.id

  and ai.id = #{assetId,jdbcType=INTEGER}


  and ai.status = #{status,jdbcType=INTEGER}


  AND j.asset_name like CONCAT(CONCAT('%',#{assetName}),'%')


  AND w.name like CONCAT(CONCAT('%',#{repositoryName}),'%')


  and ai.is_visible = #{isVisible,jdbcType=INTEGER}
